	@Test
	void test_One() {
		
		Integer[] first = {1, 2, 3};
		List<Integer> a = Arrays.asList(first);
		Integer[] query = {0,1,2};
		List<Integer> queries = Arrays.asList(query);
		Integer[] expected = {2,3,1};
		List<Integer> response = Arrays.asList(expected);
		
		assertEquals(response, subjectUnderTest.circularArrayRotation(a, 2, queries));
		
		
	}
	
	
